// service for Auth
.factory('AuthService', function ( $http, $q, $window ) {
    var userInfo;
    var decryptToken = function ( raw, pass ) {
        var token = '';
        try {
            token = CryptoJS.TripleDES
                .decrypt(raw, pass)
                .toString(CryptoJS.enc.Utf8);

        } catch (e) {}
        return token;
    };
	/*
        may be this function will change, 
        now it's almost the same as we login in application
	*/
    function login(credentials) {
        var deferred = $q.defer();

        $http
		.post("/app/login", { 
			tenantid : $window.sessionStorage.dfx_tenantid,
			appid    : $window.sessionStorage.dfx_appname,
			userid   : credentials.username,
            password : credentials.password
		})
        .then(function (answer) {
            console.log(answer)
            /*
            $http
            .get('/studio/picker/user_definition', {})
            .then(function (answer) {
                console.log('answer=',answer)

            }, function (error) {
                return deferred.reject(error);
            });    
            */    
			var accessToken = null, token = null;
			answer = answer.data;
			if ( !answer.result ) {
                return deferred.reject('server error');
            }
            if ( answer.result === 'failed' ) {
                return deferred.reject('unknown user or wrong password');
            }
            if( !answer.data ){
                return deferred.reject('unknown data');
            }
			accessToken = answer.data;	
            token = accessToken.type === 'default'
                    ? decryptToken(accessToken.token, credentials.password)
                    : accessToken.token; // type === 'plain';

			try {
                token = JSON.parse(token);
                userInfo = {
                    accessToken: token.id,
                    userName: credentials.username
                };
                $window.sessionStorage.userInfo = JSON.stringify(userInfo);
                return deferred.resolve(userInfo);
			} catch(e) {
                return deferred.reject(e);
			}

        }, function (error) {
            return deferred.reject(error);
        });

        return deferred.promise;
    }
    function getUserInfo() {
        return userInfo;
    }
    function setOperatorInfo(id, name) {
        $window.localStorage.carrierOperator = JSON.stringify({'id':id, 'name':name});
    }
    function init() {
        if ($window.sessionStorage.userInfo) {
            userInfo = JSON.parse( $window.sessionStorage["userInfo"] );
        }
    }
    init();
    return {
        login: login,
        getUserInfo: getUserInfo,
        setOperatorInfo: setOperatorInfo
    };
})
