var wMyDeclarations = angular.module("wMyDeclarations",[]);

wMyDeclarations.controller( "wMyDeclarationsController", function( $scope ) {
    // get query data
    var getQueryData = function( queryName, postData, params  ) {
		var dq = new DataQuery(queryName);
		if( postData ) {
			dq.setReqBody( postData );
        }
        if( params ) {
            dq.setParameters(params);
        }
		return dq.execute()
		.then(function() {
			var res = dq.getData();
			if( res.data ) {
				res = res.data;    
			}
			return res;
		})
		.fail(function(error){
            return error;
		});
	};

	$scope.dataMyDeclarations = {
        'id': '',
        'status': '',
        'phase': '',
        'file': '',
        'nature': '',
        'type': '',
        'date': '',
        'number': '',
        'fraction': ''
    };

    // data
    getQueryData('qGetDeclarations' )
    .then(function (result) {
        $scope.company = '';
        var company = '';
        var res = null;
        var status = '';
        var statusClass = '';
        var files = '';
        var jobs = '';
        var sep = '';
        var data = [];
        var j = 0;
        if( result.length ) {
            //console.log(result)
            for ( var i=0, len=result.length; i<len; i++ ) {

                res = result[i];

                if ( i == 0 ) {
                    company = 'SIREN : (' + res.customer +')';
                }

                for ( j=0; j<res.files.length; j++  ) {
                    files += sep + '<a href="#">'+res.files[j].name+'</a>'; 
                    sep = '<br>';
                }
                status = 'completed';
                statusClass = 'btn-success'
                for ( j=0; j<res.jobs.length; j++  ) {
                    if ( res.jobs[j].status == 'FAILED') {
                        status = 'failed';
                        statusClass = 'btn-danger';
                        break;
                    } else if (res.jobs[j].status == 'STARTED') {
                        status = 'started';
                        statusClass = 'btn-warning';
                        break;
                    }
                }
                data.push({
                    'id': res.id,
                    'status': status,
                    'statusClass': statusClass,
                    'phase': res.phase,
                    'file': files,
                    'nature': res.kind.description,
                    'type': res.type.description,
                    'date': res.created,
                    'number': res.serial,
                    'fraction': res.fractions
                });
            }
        $scope.$apply(function() {
            $scope.dataMyDeclarations = data;
            $scope.company = company;
        });
    }
    })    
    .fail(function(error){
        console.log(error);
    });




});
